function processChunk(array, start, end, callback) {
  let sum = 0;
  for (let i = start; i < end; i++) {
    sum += array[i];
  }
  callback(sum);
}

function processNonBlocking(array) {
  const chunkSize = 1000;
  let index = 0;
  let total = 0;

  function processNext() {
    const end = Math.min(index + chunkSize, array.length);
    processChunk(array, index, end, (sum) => {
      total += sum;
      index = end;

      if (index < array.length) {
        setTimeout(processNext, 0); // –Ω–µ –±–ª–æ–∫–∏—Ä—É–µ—Ç
      } else {
        console.log('Total:', total);
      }
    });
  }

  processNext();
}

