  ctx.beginPath();
  ctx.arc(x, canvas.height / 2, 20, 0, Math.PI * 2);
  ctx.fill();

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–æ–∑–∏—Ü–∏–∏
  x += 2;
  if (x > canvas.width) {
    x = 0;
  }

  // –°–ª–µ–¥—É—é—â–∏–π –∫–∞–¥—Ä
  requestAnimationFrame(animate);
}

