const canvas = document.querySelector('canvas');
const gl = canvas.getContext('webgl') || canvas.getContext('webgl2');

if (!gl) {
  console.error('WebGL not supported');
}

// –û—á–∏—Å—Ç–∫–∞ canvas
gl.clearColor(0.0, 0.0, 0.0, 1.0); // —á–µ—Ä–Ω—ã–π —Ü–≤–µ—Ç
gl.clear(gl.COLOR_BUFFER_BIT);

// –£—Å—Ç–∞–Ω–æ–≤–∫–∞ viewport
gl.viewport(0, 0, canvas.width, canvas.height);

// –í–µ—Ä—à–∏–Ω–Ω—ã–π —à–µ–π–¥–µ—Ä (GLSL)
const vertexShaderSource = `
  attribute vec2 a_position;

  void main() {
    gl_Position = vec4(a_position, 0.0, 1.0);
  }
`;

// –ö–æ–º–ø–∏–ª—è—Ü–∏—è —à–µ–π–¥–µ—Ä–∞
function createShader(gl, type, source) {
  const shader = gl.createShader(type);
  gl.shaderSource(shader, source);
  gl.compileShader(shader);

  if (!gl.getShaderParameter(shader, gl.COMPILE_STATUS)) {
    console.error('Shader compile error:', gl.getShaderInfoLog(shader));
    gl.deleteShader(shader);
    return null;
  }

  return shader;
}

const vertexShader = createShader(gl, gl.VERTEX_SHADER, vertexShaderSource);

// –§—Ä–∞–≥–º–µ–Ω—Ç–Ω—ã–π —à–µ–π–¥–µ—Ä (GLSL)
const fragmentShaderSource = `
  precision mediump float;

  uniform vec4 u_color;

  void main() {
    gl_FragColor = u_color;
  }
`;

const fragmentShader = createShader(gl, gl.FRAGMENT_SHADER, fragmentShaderSource);

// –°–æ–∑–¥–∞–Ω–∏–µ –ø—Ä–æ–≥—Ä–∞–º–º—ã
function createProgram(gl, vertexShader, fragmentShader) {
  const program = gl.createProgram();
  gl.attachShader(program, vertexShader);
  gl.attachShader(program, fragmentShader);
  gl.linkProgram(program);

  if (!gl.getProgramParameter(program, gl.LINK_STATUS)) {
    console.error('Program link error:', gl.getProgramInfoLog(program));
    gl.deleteProgram(program);
    return null;
  }

  return program;
}

const program = createProgram(gl, vertexShader, fragmentShader);
gl.useProgram(program);

// –í–µ—Ä—à–∏–Ω—ã —Ç—Ä–µ—É–≥–æ–ª—å–Ω–∏–∫–∞
const positions = [
  0, 0.5,   // –≤–µ—Ä—Ö
  -0.5, -0.5, // –ª–µ–≤—ã–π –Ω–∏–∑
  0.5, -0.5   // –ø—Ä–∞–≤—ã–π –Ω–∏–∑
];

// –°–æ–∑–¥–∞–Ω–∏–µ –±—É—Ñ–µ—Ä–∞
const positionBuffer = gl.createBuffer();
gl.bindBuffer(gl.ARRAY_BUFFER, positionBuffer);
gl.bufferData(gl.ARRAY_BUFFER, new Float32Array(positions), gl.STATIC_DRAW);

// –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –∞—Ç—Ä–∏–±—É—Ç–∞
const positionLocation = gl.getAttribLocation(program, 'a_position');
gl.enableVertexAttribArray(positionLocation);
gl.vertexAttribPointer(positionLocation, 2, gl.FLOAT, false, 0, 0);

// –£—Å—Ç–∞–Ω–æ–≤–∫–∞ —Ü–≤–µ—Ç–∞
const colorLocation = gl.getUniformLocation(program, 'u_color');
gl.uniform4f(colorLocation, 1.0, 0.0, 0.0, 1.0); // –∫—Ä–∞—Å–Ω—ã–π

// –û—Ç—Ä–∏—Å–æ–≤–∫–∞
gl.drawArrays(gl.TRIANGLES, 0, 3);

// –ú–∞—Ç—Ä–∏—Ü–∞ –ø—Ä–æ–µ–∫—Ü–∏–∏ (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è)
function createProjectionMatrix(width, height) {
  return [
    2 / width, 0, 0, 0,
    0, 2 / height, 0, 0,
    0, 0, 1, 0,
    -1, -1, 0, 1
  ];
}

// –í–µ—Ä—à–∏–Ω–Ω—ã–π —à–µ–π–¥–µ—Ä —Å –º–∞—Ç—Ä–∏—Ü–µ–π
const vertexShaderWithMatrix = `
  attribute vec2 a_position;
  uniform mat4 u_matrix;

  void main() {
    gl_Position = u_matrix * vec4(a_position, 0.0, 1.0);
  }
`;

// –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –º–∞—Ç—Ä–∏—Ü—ã
const matrixLocation = gl.getUniformLocation(program, 'u_matrix');
const matrix = createProjectionMatrix(canvas.width, canvas.height);
gl.uniformMatrix4fv(matrixLocation, false, matrix);

// –°–æ–∑–¥–∞–Ω–∏–µ —Ç–µ–∫—Å—Ç—É—Ä—ã
const texture = gl.createTexture();
gl.bindTexture(gl.TEXTURE_2D, texture);

// –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è
const image = new Image();
image.onload = () => {
  gl.bindTexture(gl.TEXTURE_2D, texture);
  gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, image);
  gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
  gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
  gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
};
image.src = 'texture.png';

