/* –ö–û–ú–ü–û–ù–ï–ù–¢–ù–´–ô –ü–û–î–•–û–î (—Å–æ–≤—Ä–µ–º–µ–Ω–Ω—ã–π) */
/* –ü–µ—Ä–µ–∏—Å–ø–æ–ª—å–∑—É–µ–º—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç */

function Button({ children, variant = 'primary' }) {
