// ‚úÖ Controlled: —Å–ª–æ–∂–Ω–∞—è —Ñ–æ—Ä–º–∞ —Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π
function ComplexForm() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  // –í–∞–ª–∏–¥–∞—Ü–∏—è, —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è –ø–æ–ª–µ–π
  const handleChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (field === 'password' && value.length < 8) {
      setError('–ü–∞—Ä–æ–ª—å —Å–ª–∏—à–∫–æ–º –∫–æ—Ä–æ—Ç–∫–∏–π');
    }

    // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è
    if (field === 'password') {
      setFormData(prev => ({
        ...prev,
        confirmPassword: '' // –°–±—Ä–æ—Å –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
      }));
    }
  };

  return (
    <form>
      <input 
        value={formData.email}
        onChange={e => handleChange('email', e.target.value)}
      />
      {/* ... */}
    </form>
  );
}

