function TodoList({ todos, filter }) {
  // –ü–µ—Ä–µ—Å—á–∏—Ç—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ –∫–∞–∂–¥–æ–º —Ä–µ–Ω–¥–µ—Ä–µ, –¥–∞–∂–µ –µ—Å–ª–∏ todos –∏ filter –Ω–µ –∏–∑–º–µ–Ω–∏–ª–∏—Å—å
  const filteredTodos = todos.filter(todo => 
