type Action = { type: string; payload?: unknown };

type Listener = () => void;

class Dispatcher {
  private listeners: Listener[] = [];

  dispatch(action: Action) {
    // Command: –¥–µ–π—Å—Ç–≤–∏–µ –∫–∞–∫ –æ–±—ä–µ–∫—Ç
    // Observer: –æ–ø–æ–≤–µ—â–µ–Ω–∏–µ –ø–æ–¥–ø–∏—Å—á–∏–∫–æ–≤
