type Context = { user?: { id: string }; requestId: string };

type Middleware = (ctx: Context, next: () => Promise<void>) => Promise<void>;

const logger: Middleware = async (ctx, next) => {
  console.log('start', ctx.requestId);
  await next();
  console.log('end', ctx.requestId);
};

const auth: Middleware = async (ctx, next) => {
  if (!ctx.user) {
    throw new Error('Unauthorized');
  }
  await next();
};

function compose(middlewares: Middleware[]): Middleware {
  return async (ctx, next) => {
    let index = -1;
    async function dispatch(i: number): Promise<void> {
      if (i <= index) return;
      index = i;
      const fn = middlewares[i] || next;
      if (!fn) return;
      await fn(ctx, () => dispatch(i + 1));
    }
    await dispatch(0);
  };
}

function withLogger<P>(Component: React.ComponentType<P>) {
  return function Wrapped(props: P) {
    console.log('render', Component.displayName || Component.name);
    return <Component {...props} />;
  };
}

