function applyOptimisticCreate(list: Item[], temp: Item): Item[] {
  // –¥–æ–±–∞–≤–ª—è–µ–º –≤—Ä–µ–º–µ–Ω–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç —Å–æ —Å—Ç–∞—Ç—É—Å–æ–º pending
  return [...list, temp];
