// Composable: –ø–µ—Ä–µ–∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –ª–æ–≥–∏–∫–∏
export function useUserData(userId) {
  const user = ref(null);
  const loading = ref(true);

  onMounted(async () => {
    const response = await fetch(`/api/users/${userId}`);
    user.value = await response.json();
    loading.value = false;
  });

  return { user, loading };
}

