interface ValidationStrategy {
  validate(value: string): boolean;
}

// –ö–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ —Å—Ç—Ä–∞—Ç–µ–≥–∏–∏
class EmailValidation implements ValidationStrategy {
  validate(value: string): boolean {
    return value.includes('@') && value.includes('.');
  }
}

class PhoneValidation implements ValidationStrategy {
  validate(value: string): boolean {
    return /^+?[ds-()]+$/.test(value);
  }
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∏—Å–ø–æ–ª—å–∑—É–µ—Ç —Å—Ç—Ä–∞—Ç–µ–≥–∏—é
function Input({ value, onChange, validator }: {
  value: string;
  onChange: (value: string) => void;
  validator: ValidationStrategy;
}) {
  const isValid = validator.validate(value);

  return (
    <div>
      <input value={value} onChange={(e) => onChange(e.target.value)} />
      {!isValid && <span>–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç</span>}
    </div>
  );
}

