// Reducer - —á–∏—Å—Ç–∞—è —Ñ—É–Ω–∫—Ü–∏—è
function counterReducer(state = { count: 0 }, action) {
  switch (action.type) {
    case 'INCREMENT':
      return { count: state.count + 1 };
    case 'DECREMENT':
      return { count: state.count - 1 };
    case 'RESET':
      return { count: 0 };
    default:
      return state; // –í–∞–∂–Ω–æ: –≤–æ–∑–≤—Ä–∞—â–∞—Ç—å state, –µ—Å–ª–∏ action –Ω–µ —Ä–∞—Å–ø–æ–∑–Ω–∞–Ω
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
let state = counterReducer(undefined, { type: 'INCREMENT' }); // { count: 1 }
state = counterReducer(state, { type: 'INCREMENT' }); // { count: 2 }
state = counterReducer(state, { type: 'DECREMENT' }); // { count: 1 }
state = counterReducer(state, { type: 'RESET' }); // { count: 0 }

// –ü—Ä–µ–¥—Å–∫–∞–∑—É–µ–º–æ—Å—Ç—å: –≤—Å–µ–≥–¥–∞ –æ–¥–∏–Ω–∞–∫–æ–≤—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –¥–ª—è –æ–¥–∏–Ω–∞–∫–æ–≤—ã—Ö –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö

// Reducer —Å –¥–∞–Ω–Ω—ã–º–∏ –≤ action
function todosReducer(state = { todos: [] }, action) {
  switch (action.type) {
    case 'ADD_TODO':
      return {
        todos: [...state.todos, {
          id: action.payload.id,
          text: action.payload.text,
          completed: false
        }]
      };
    case 'TOGGLE_TODO':
      return {
        todos: state.todos.map(todo =>
          todo.id === action.payload.id
            ? { ...todo, completed: !todo.completed }
            : todo
        )
      };
    case 'DELETE_TODO':
      return {
        todos: state.todos.filter(todo => todo.id !== action.payload.id)
      };
    default:
      return state;
  }
}

