    requestIdleCallback(() => {
      const end = performance.now();
      const hydrationTime = end - start;

      console.log('Hydration time:', hydrationTime, 'ms');

      // –û—Ç–ø—Ä–∞–≤–∫–∞ –º–µ—Ç—Ä–∏–∫–∏
      if (window.gtag) {
        gtag('event', 'hydration_time', {
          value: Math.round(hydrationTime)
        });
      }
    });
  }, []);
}

