// fetch('https://bank.com/transfer', { ... })
// Origin: https://trusted-domain.com
// –°–µ—Ä–≤–µ—Ä –æ–±—Ä–∞–±–æ—Ç–∞–µ—Ç –∑–∞–ø—Ä–æ—Å ‚úÖ

// Referer —Å–æ–¥–µ—Ä–∂–∏—Ç –ø–æ–ª–Ω—ã–π URL –∏—Å—Ç–æ—á–Ω–∏–∫–∞
// Referer: https://trusted-domain.com/page

app.post('/transfer', (req, res) => {
  const referer = req.headers.referer;

  if (!referer) {
    // Referer –º–æ–∂–µ—Ç –æ—Ç—Å—É—Ç—Å—Ç–≤–æ–≤–∞—Ç—å (privacy settings, HTTPS->HTTP)
    // –ù–µ –ø–æ–ª–∞–≥–∞—Ç—å—Å—è —Ç–æ–ª—å–∫–æ –Ω–∞ Referer
    return res.status(403).json({ error: 'Missing referer' });
  }

  const refererUrl = new URL(referer);
