// –ü—Ä–æ—Å—Ç–æ–π rate limiter
class RateLimiter {
  private requests: number[] = [];
  private maxRequests: number;
  private windowMs: number;

  constructor(maxRequests: number, windowMs: number) {
    this.maxRequests = maxRequests;
    this.windowMs = windowMs;
  }

  async check(): Promise<boolean> {
    const now = Date.now();
    this.requests = this.requests.filter(
      time => now - time < this.windowMs
    );

    if (this.requests.length >= this.maxRequests) {
      return false; // –õ–∏–º–∏—Ç –ø—Ä–µ–≤—ã—à–µ–Ω
    }

    this.requests.push(now);
    return true;
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
const limiter = new RateLimiter(10, 60000); // 10 –∑–∞–ø—Ä–æ—Å–æ–≤ –≤ –º–∏–Ω—É—Ç—É

async function makeRequest() {
  if (!(await limiter.check())) {
    throw new Error('Rate limit exceeded');
  }

  const response = await fetch('/api/data');

  if (response.status === 429) {
    // –°–µ—Ä–≤–µ—Ä —Ç–æ–∂–µ –æ–≥—Ä–∞–Ω–∏—á–∏–ª
    const retryAfter = response.headers.get('Retry-After');
    await new Promise(resolve => 
      setTimeout(resolve, parseInt(retryAfter || '60') * 1000)
    );
    return makeRequest();
  }

  return response.json();
}

