# nginx.conf

# –í–∫–ª—é—á–µ–Ω–∏–µ —Å–∂–∞—Ç–∏—è —Å –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º
location ~* \.(js|css|html|json)$ {
  # –ü—Ä–æ–≤–µ—Ä–∫–∞ Accept-Encoding
  gzip on;
  gzip_vary on;
  gzip_types text/plain text/css application/json application/javascript;

  # Brotli (–µ—Å–ª–∏ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω –º–æ–¥—É–ª—å)
  brotli on;
  brotli_types text/plain text/css application/json application/javascript;

  # –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç: Brotli > Gzip
  # nginx –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –≤—ã–±–∏—Ä–∞–µ—Ç –Ω–∞ –æ—Å–Ω–æ–≤–µ Accept-Encoding
}

# –õ–æ–∫–∞–ª–∏–∑–∞—Ü–∏—è
location / {
  # –ü—Ä–æ–≤–µ—Ä–∫–∞ Accept-Language
  if ($http_accept_language ~* ^ru) {
    set $lang "ru";
  }
  if ($http_accept_language ~* ^en) {
    set $lang "en";
  }

  # –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–µ–≥–æ —Ñ–∞–π–ª–∞
  try_files /$lang$uri /$uri =404;
}

// Express.js –ø—Ä–∏–º–µ—Ä
app.get('/api/users', (req, res) => {
  const accept = req.headers.accept || '';
  const acceptEncoding = req.headers['accept-encoding'] || '';

  // –í—ã–±–æ—Ä —Ñ–æ—Ä–º–∞—Ç–∞
  let contentType = 'application/json';
  if (accept.includes('application/xml')) {
    contentType = 'application/xml';
  }

  // –í—ã–±–æ—Ä —Å–∂–∞—Ç–∏—è
  let encoding = null;
  if (acceptEncoding.includes('br')) {
    encoding = 'br';
  } else if (acceptEncoding.includes('gzip')) {
    encoding = 'gzip';
  }

  const data = { users: [...] };
  let response = JSON.stringify(data);

  // –°–∂–∞—Ç–∏–µ
  if (encoding === 'br') {
    response = brotliCompress(response);
    res.setHeader('Content-Encoding', 'br');
  } else if (encoding === 'gzip') {
    response = gzipCompress(response);
    res.setHeader('Content-Encoding', 'gzip');
  }

  res.setHeader('Content-Type', contentType);
  res.send(response);
});

