app.get('/api/users', async (req, res) => {
  const start = Date.now();
  try {
    const users = await db.users.findMany();
    const duration = Date.now() - start;

    // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ:
    logger.info({
      endpoint: '/api/users',
      duration,
      dbQueries: 1,
      status: 200
    });

    res.json(users);
  } catch (error) {
    logger.error({
      endpoint: '/api/users',
      error: error.message,
      status: 500
    });
    res.status(500).json({ error: 'Internal error' });
  }
});

