}

// –ú–µ—Ç–æ–¥—ã –º–∞—Å—Å–∏–≤–æ–≤
arr.map(x => x * 2); // O(n)
arr.filter(x => x > 2); // O(n)
arr.find(x => x === 3); // O(n) –≤ —Ö—É–¥—à–µ–º —Å–ª—É—á–∞–µ
arr.includes(3); // O(n)
arr.indexOf(3); // O(n)

// Object.keys
Object.keys({ a: 1, b: 2 }); // O(n)

// –í–ª–æ–∂–µ–Ω–Ω—ã–µ —Ü–∏–∫–ª—ã
const arr = [1, 2, 3];
for (let i = 0; i < arr.length; i++) {
  for (let j = 0; j < arr.length; j++) {
    console.log(arr[i], arr[j]); // O(n¬≤) - n * n –æ–ø–µ—Ä–∞—Ü–∏–π
  }
}

// –ù–µ–∫–æ—Ç–æ—Ä—ã–µ –∞–ª–≥–æ—Ä–∏—Ç–º—ã —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏
arr.sort(); // O(n log n) –∏–ª–∏ O(n¬≤) –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏

